import React, { useState } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

function Signup() {
  const [data, setData] = useState({
    UserName: "",
    email: "",
    password: "",
  });
  const [error, setError] = useState("");
  const history = useHistory(); // Use useHistory hook for navigation

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:3001/signup";
      const { data: res } = await axios.post(url, data);
      console.log(res.message); // Optional: Log the success message
      history.push("/login"); // Use history.push for redirection
    } catch (error) {
      console.error(error.response);  // Log the full error response for debugging
      if (error.response && error.response.status >= 400 && error.response.status <= 500) {
        setError(error.response.data.message);
      } else {
        setError("An unexpected error occurred. Please try again later.");
      }
    }
  };

  return (
    <div className="signup background">
      <form className="background" onSubmit={handleSubmit}>
        <div className="title">Register</div>
        <div className="inputs">
          <input
            required
            type="text"
            name="UserName"
            placeholder="User Name"
            onChange={handleChange}
            value={data.UserName}
          />
          <input
            required
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleChange}
            value={data.email}
          />
          <input
            required
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
            value={data.password}
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit">Sign Up</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login</Link>
      </p>
    </div>
  );
}

export default Signup;
