import React from 'react'
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


function Login() {
  const [data, setData] = useState({ email: "", password: "" });
	const [error, setError] = useState("");

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:3000/login";
			const { data: res } = await axios.post(url, data);
			localStorage.setItem("token", res.data);
			window.location = "/";
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};
  return (
    <div class="signup backround">
         <form action='./' class="backround"  onSubmit={handleSubmit}>
            <div class="title">
                Log In
            </div>
            <div class="inputs">
            <input required type="email" placeholder='email' onChange={handleChange}
							value={data.email}></input>
            <input required type="password" placeholder='password' onChange={handleChange}
							value={data.password}></input>
            </div>
            <button type="submit">Login</button>
        </form>
        <p>Don't have an account? <Link to="/signup">Signup</Link></p>
    </div>
  )
}

export default Login
