import React from 'react'
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useNavigate } from 'react-router-dom';



function Signup() {
  const [data, setData] = useState({
		userName: "",
		email: "",
		password: "",
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.username]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:3000/signup";
			const { data: res } = await axios.post(url, data);
			navigate("/login");
			console.log(res.message);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};
  return (
    <>
        <div class="signup backround">
        <form action='./' class="backround" onSubmit={handleSubmit}>
            <div class="title">
                Register
            </div>
            <div class="inputs">
            <input required type="text" placeholder='username' onChange={handleChange} value={data.userName}></input>
            <input required type="email" placeholder='email' onChange={handleChange} value={data.email}></input>
            <input required type="password" placeholder='password' onChange={handleChange} value={data.password}></input>
            </div>
            <button type="submit">Sign up</button>
        </form>
        <p>Already have an account? <Link to="/login">Login</Link></p>
        </div>
    </>
  )
}

export default Signup
